mapper.select:
SELECT a.id,
o.goods_title AS goodsTitle,
a.name,
a.date,
a.status,
r.file_path AS filePath
FROM tb_appointment a
JOIN tb_order o ON a.order_id = o.id
LEFT JOIN tb_checkup_report r ON a.id = r.appointment_id
WHERE o.customer_id = #{customerId}

    AND a.status = #{status}


    AND a.date = #{date}


    AND (
    o.goods_title LIKE CONCAT( "%", #{keyword}, "%" ) OR
    a.`name` = #{keyword} OR
    a.tel = #{keyword}
    )

ORDER BY a.id DESC
LIMIT #{start}, #{length}

SELECT COUNT(*)
FROM tb_appointment a
JOIN tb_order o ON a.order_id = o.id
WHERE o.customer_id = #{customerId}

    AND a.status = #{status}


    AND a.date = #{date}


    AND (
    o.goods_title LIKE CONCAT( "%", #{keyword}, "%" ) OR
    a.`name` = #{keyword} OR
    a.tel = #{keyword}
    )

SELECT a.id, a.name, a.sex, CONCAT(SUBSTRING(a.tel,1,3),"****",SUBSTRING(a.tel,8,4)) AS tel, CONCAT(SUBSTRING(a.pid,1,3),"***********",SUBSTRING(a.pid,15,4)) AS pid, TIMESTAMPDIFF(YEAR,a.birthday,NOW()) AS age, a.company, a.status, o.goods_title AS title, o.snapshot_id AS snapshotId FROM tb_appointment a JOIN tb_order o ON a.order_id = o.id
AND a.date = #{date}
AND a.name LIKE CONCAT("%",#{name},"%")
AND a.tel = #{tel}
AND a.status = #{status}
ORDER BY a.id DESC LIMIT #{start}, #{length}

SELECT COUNT(*)
FROM tb_appointment


        AND date = #{date}


        AND name LIKE CONCAT("%",#{name},"%")


        AND tel = #{tel}


        AND status = #{status}

mapper.delete:
DELETE FROM tb_appointment WHERE id IN
#{one}
AND status = 1

mapper.select:
SELECT id, status
FROM tb_appointment
WHERE date = CURRENT_DATE()
  AND pid = #{pid}
  AND name = #{name}
  AND sex = #{sex}
    LIMIT 1

mapper.update:
UPDATE tb_appointment
SET status = #{status}
WHERE uuid = #{uuid}
  AND status != 3
