mapper.select:
SELECT r.id, a.name, a.sex, TIMESTAMPDIFF(YEAR,a.birthday,NOW()) AS age, CONCAT(SUBSTRING(a.pid,1,3),"***********",SUBSTRING(a.pid,15,4)) AS pid, a.tel, CONCAT(SUBSTRING(a.tel,1,3),"****",SUBSTRING(a.tel,8,4)) AS hideTel, a.mailing_address AS mailingAddress, a.date, r.status, r.file_path AS filePath, r.waybill_code AS waybillCode, r.waybill_date AS waybillDate FROM tb_checkup_report r JOIN tb_appointment a ON r.appointment_id = a.id
AND a.name LIKE CONCAT("%",#{name},"%")
AND a.tel = #{tel}
AND r.waybill_code = #{waybillCode}
AND r.status = #{status}
ORDER BY r.id DESC LIMIT #{start}, #{length}

SELECT COUNT(*)
FROM tb_checkup_report r
JOIN tb_appointment a ON r.appointment_id = a.id


        AND a.name LIKE CONCAT("%",#{name},"%")


        AND a.tel = #{tel}


        AND r.waybill_code = #{waybillCode}


        AND r.status = #{status}

mapper.update:
UPDATE tb_checkup_report
SET waybill_code = #{waybillCode},
    waybill_date = #{waybillDate}
WHERE appointment_id = #{appointmentId}
