mapper.select:
SELECT r.id, r.name, IFNULL(tmp.count, 0) AS count, r.remark FROM tb_rule r LEFT JOIN ( SELECT rule_id, COUNT(*) AS count FROM tb_goods GROUP BY rule_id ) tmp ON r.id = tmp.rule_id
AND r.name LIKE CONCAT('%',#{name},'%')
LIMIT #{start}, #{length}

SELECT name, rule, remark
FROM tb_rule
WHERE id = #{id}

mapper.update:
UPDATE tb_rule
SET name = #{name},
    rule = #{rule},
    remark = #{remark}
WHERE id = #{id}
